	STRUCT
		iWordsNo 	: INT;
		arrData		: ARR_B_0_13;
	END_STRUCT;


	udtBufferSend :
	STRUCT
		iWordsNo 	: INT;
		arrData		: ARR_B_0_13;
	END_STRUCT;
